  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'maniac',
	title: 'Game Guide - Maniac Mansion',
	sidebar_label: 'Maniac Mansion'
};
export const metadata = {
  "id": "maniac",
  "title": "Game Guide - Maniac Mansion",
  "description": "About",
  "source": "@site/docs/maniac.md",
  "permalink": "/maniac",
  "editUrl": "https://github.com/facebook/docusaurus/edit/master/website/docs/maniac.md",
  "sidebar_label": "Maniac Mansion"
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'About',
  id: 'about',
  children: []
}, {
  value: 'Where can I get it?',
  id: 'where-can-i-get-it',
  children: []
}, {
  value: 'Hotkeys',
  id: 'hotkeys',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "about"
    }}>{`About`}</h2>
    <hr></hr>
    <h2 {...{
      "id": "where-can-i-get-it"
    }}>{`Where can I get it?`}</h2>
    <hr></hr>
    <h2 {...{
      "id": "hotkeys"
    }}>{`Hotkeys`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": "center"
          }}>{`Hotkey`}</th>
          <th parentName="tr" {...{
            "align": "center"
          }}>{`Action`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Alt-x`}</td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Quit`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Ctrl 0-9 and Alt 0-9`}</td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Load and save game state`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Ctrl-d`}</td>
          <td parentName="tr" {...{
            "align": "center"
          }}>{`Starts the debugger`}</td>
        </tr>
      </tbody>
    </table>
    <pre><code parentName="pre" {...{}}>{`Ctrl-f                 - Toggle fast mode
Ctrl-g                 - Runs in really REALLY fast mode
Ctrl-t                 - Switch between 'Speech only',
                         'Speech and Subtitles' and 'Subtitles only'
Tilde (~)              - Show/hide the debugging console
[ and ]                - Music volume, down/up
- and +                - Text speed, slower/faster
F5                     - Displays a save/load box
Alt-F5                 - Displays the original save/load box, if the
                         game has one. You can save and load games using
                         this, however it is not intended for this purpose,
                         and may even crash ScummVM in some games.
i                      - Displays IQ points (Indiana Jones and the Last
                         Crusade, and Indiana Jones and the Fate of
                         Atlantis)
Space                  - Pauses
Period (.)             - Skips current line of text in some games
Enter                  - Simulate left mouse button press
Tab                    - Simulate right mouse button press
`}</code></pre>
    <p>{`Languages:`}</p>
    <p>{`Maniac Mansion and Zak McKracken
en  - English (default)
de  - German
fr  - French
it  - Italian
es  - Spanish`}</p>
    <p>{`Autosave: supported
List Saves: supported
Slot Save: supported`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
