	id: 'raspberrypi',
	title: 'Quickstart Guide - Raspberry Pi',
	sidebar_label: 'Raspberry Pi'
};
export const metadata = {
  "id": "installation/raspberrypi",
  "title": "Quickstart Guide - Raspberry Pi",
  "description": "Installing ScummVM",
