  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'general_options',
	title: 'General options',
	sidebar_label: 'General options'
};
export const metadata = {
  "id": "using/general_options",
  "title": "General options",
  "description": "The ScummVM Launcher gives us a graphical method for changing the settings that it uses to run games. There are two ways to do this",
  "source": "@site/docs/using/general_options.md",
  "permalink": "/ScummDocs/using/general_options",
  "editUrl": "https://github.com/ScummDocs/docs/using/general_options.md",
  "sidebar_label": "General options",
  "sidebar": "someSidebar",
  "previous": {
    "title": "The ScummVM Interface",
    "permalink": "/ScummDocs/using/launcher"
  },
  "next": {
    "title": "Game settings",
    "permalink": "/ScummDocs/using/game_settings"
