  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'queen',
	title: 'Game Guide - Flight of the Amazon Queen',
	sidebar_label: 'Flight of the Amazon Queen'
};
export const metadata = {
  "id": "queen",
  "title": "Game Guide - Flight of the Amazon Queen",
  "description": "About",
  "source": "@site/docs/queen.md",
  "permalink": "/queen",
  "editUrl": "https://github.com/ScummDocs/docs/queen.md",
  "sidebar_label": "Flight of the Amazon Queen"
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'About',
  id: 'about',
  children: []
}, {
  value: 'Availability',
  id: 'availability',
  children: []
}, {
  value: 'Data Files',
  id: 'data-files',
  children: []
}, {
  value: 'Hotkeys',
  id: 'hotkeys',
  children: []
}, {
  value: 'Settings',
  id: 'settings',
  children: [{
    value: 'Autosave',
    id: 'autosave',
    children: []
  }, {
    value: 'Viewing/Loading saved games from the command line',
    id: 'viewingloading-saved-games-from-the-command-line',
    children: []
  }, {
    value: 'Non-standard Settings Keywords',
    id: 'non-standard-settings-keywords',
    children: []
  }]
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "about"
    }}>{`About`}</h2>
    <p>{`Flight of the Amazon Queen follows a pilot for hire named Joe King who is hired to fly a famous actress to her next job, but ends up in a lightning storm and crashes deep in the Amazon Jungle. In the jungle, Joe uncovers a plot by a mad scientist to take over the world by creating an army of dinosaur women created from Amazon women`}</p>
    <h2 {...{
      "id": "availability"
    }}>{`Availability`}</h2>
    <p>{`In March 2004, the creators and copyright holders of Flight of the Amazon Queen, John Passfield and Steve Stamatiadis, released the game as freeware for use with ScummVM. It is available for download from our `}<a parentName="p" {...{
        "href": "https://www.scummvm.org/games/"
      }}>{`Downloads`}</a>{` page.`}</p>
    <hr></hr>
    <h2 {...{
      "id": "data-files"
    }}>{`Data Files`}</h2>
    <p>{`[Required Data Files]`}{`(`}</p>
    <p>{`Only the original non-freeware version of Flight of the Amazon Queen (from original CD), requires the queen.tbl datafile (available from the Downloads page on our website) in either the directory containing the queen.1 game data file, in your extrapath, or in the directory where your ScummVM executable resides.`}</p>
    <p>{`Alternatively, you can use the compress_queen tool from the tools package to 'rebuild' your FOTAQ data file to include the table for that specific version, and thus removing the run-time dependency on the queen.tbl file. This tool also allows you to compress the speech and sound effects with MP3, OGG or FLAC.`}</p>
    <hr></hr>
    <h2 {...{
      "id": "hotkeys"
    }}>{`Hotkeys`}</h2>
    <pre><code parentName="pre" {...{}}>{`Ctrl-d                 - Starts the debugger
Ctrl-f                 - Toggle fast mode
F1                     - Use Journal (saving/loading)
F11                    - Quicksave
F12                    - Quickload
Escape                 - Skips cutscenes
Space                  - Skips current line of text
`}</code></pre>
    <hr></hr>
    <h2 {...{
      "id": "settings"
    }}>{`Settings`}</h2>
    <h3 {...{
      "id": "autosave"
    }}>{`Autosave`}</h3>
    <p>{`For some games ScummVM will by default automatically save the current state every five minutes (adjustable via the autosave_period config setting). The default autosave slot for many engines is slot 0.`}</p>
    <h3 {...{
      "id": "viewingloading-saved-games-from-the-command-line"
    }}>{`Viewing/Loading saved games from the command line`}</h3>
    <p><inlineCode parentName="p">{`--list-saves`}</inlineCode></p>
    <p>{`This switch may be used to display a list of the current saved games of the specified target game and their corresponding save slots. If no target is specified, it lists saved games for all known target.`}</p>
    <p>{`Usage: --list-saves --game=`}{`[TARGET]`}{`, where `}{`[TARGET]`}{` is the target game.`}</p>
    <p><inlineCode parentName="p">{`--save-slot/-x`}</inlineCode></p>
    <p>{`This switch may be used to load a saved game directly from the command line.`}</p>
    <p>{`Usage: --save-slot`}{`[SLOT]`}{` or -x`}{`[SLOT]`}{`, where `}{`[SLOT]`}{` is the save slot number.`}</p>
    <h3 {...{
      "id": "non-standard-settings-keywords"
    }}>{`Non-standard Settings Keywords`}</h3>
    <pre><code parentName="pre" {...{}}>{`music_mute         bool     If true, music is muted
sfx_mute           bool     If true, sound effects are muted
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
