  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: '_index',
	title: 'Proposal GSOD 2020'
};
export const metadata = {
  "id": "_index",
  "title": "Proposal GSOD 2020",
  "description": "Objectives",
  "source": "@site/docs/_index.md",
  "permalink": "/_index",
  "editUrl": "https://github.com/ScummDocs/docs/_index.md",
  "next": {
    "title": "What is ScummVM?",
    "permalink": "/about/about"
  }
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'Objectives',
  id: 'objectives',
  children: []
}, {
  value: 'Areas of Focus',
  id: 'areas-of-focus',
  children: []
}, {
  value: 'Plan of Attack',
  id: 'plan-of-attack',
  children: []
}, {
  value: 'Why me?',
  id: 'why-me',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h3 {...{
      "id": "objectives"
    }}>{`Objectives`}</h3>
    <ol>
      <li parentName="ol">{`To produce high-quality end user documentation by consolidating large amounts of information from disparate sources and re-writing information for clarity and consistency. Documentation should be easy to understand for beginners, without being patronizing to tech-savvy users. `}</li>
      <li parentName="ol">{`To create a documentation portal using a static-site generator such as Docusaurus or ReadtheDocs.`}</li>
      <li parentName="ol">{`To future-proof end user documentation by creating templates and detailed how-to instructions for future open source contributors.`}</li>
    </ol>
    <h3 {...{
      "id": "areas-of-focus"
    }}>{`Areas of Focus`}</h3>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`New users need a comprehensive guide for installing ScummVM on any of the supported platforms, especially in cases where the install is not intuitive, or the user may not have a strong technical base. `}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Advanced users need additional behind-the-scenes options to tweak how their games run, including how to report any bugs. `}</p>
      </li>
      <li parentName="ol">
        <p parentName="li">{`Information relating to each supported game needs to be in one place - a "one-stop-shop" approach - including, but not limited to;`}</p>
      </li>
    </ol>
    <ul>
      <li parentName="ul">{`What is the game about, and if the user doesn't have the game, where can they get it?`}</li>
      <li parentName="ul">{`Is there anything unusual the user needs to know about the install?`}</li>
      <li parentName="ul">{`What are the engine-specific controls the user should know about?`}</li>
      <li parentName="ul">{`Are there any known problems with the game when played via ScummVM?`}</li>
    </ul>
    <h3 {...{
      "id": "plan-of-attack"
    }}>{`Plan of Attack`}</h3>
    <p>{`I am in a position where I can work on this project full-time for the span of the three month GSOD timeline. `}</p>
    <p>{`I propose to start by combing the existing documentation; Readme, Website and Wiki. As I go, I will re-write and add to each section as required. I will ensure that information gathered from these pre-existing sources is removed on completion of the project to prevent double-ups and contradictions.`}</p>
    <p>{`Once this preliminary work is done, I will coordinate with developers and port maintainers to ensure the information presented in the documentation is as comprehensive as possible. `}</p>
    <h3 {...{
      "id": "why-me"
    }}>{`Why me?`}</h3>
    <p>{`Although I do not have prior work examples to provide in this proposal, I have created a sample site to show my vision for the project. Navigate to the sample documentation by clicking "Next" at the bottom of the page, or "About" on the top navigation bar.`}</p>
    <p>{`I believe I am well suited to the project because:`}</p>
    <ul>
      <li parentName="ul">{`The ScummVM project personally interests me. `}<ul parentName="li">
          <li parentName="ul">{`I believe you work better when you care about what you're working on. I grew up on retro games in the early 90s! `}</li>
        </ul></li>
      <li parentName="ul">{`I'm good at taking complex information and making it easy to understand. `}<ul parentName="li">
          <li parentName="ul">{`In the past I wrote aviation Operating Manuals as part of my work.`}</li>
        </ul></li>
      <li parentName="ul">{`I have an innate curiosity for things I don't know, and a willingness to learn. `}<ul parentName="li">
          <li parentName="ul">{`This will serve me well as I delve deeper into the workings of ScummVM. Just in the course of writing this proposal, I taught myself how to set up a Raspberry Pi, use it as a server, and create a Docusaurus site (including basic JS!).`}</li>
        </ul></li>
      <li parentName="ul">{`I'm an excellent communicator. `}<ul parentName="li">
          <li parentName="ul">{`This is critical for both the writing of the documentation, but also for getting the right information from the subject matter experts. `}</li>
        </ul></li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
