  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = '<img alt=" " src={useBaseUrl(\'img/Launcher/Loadlist.jpg\')} />\nThis menu allows you to run the highlighted game from a previously saved state straight from the Launcher, without having to start the game first. Here you can usually see the names of your savegames, their timestamps and screenshots to aid you in finding the exact state. When you have located the desired saved game, just select it and press Load to launch the game using that state. Besides loading a previous state, you can also delete obsolete saved games by highlighting them and clicking Delete.\nNote that there are games that do not support this feature altogether, or whose saved states lack some information (e.g. playtime, screenshots).';
export const metadata = {
  "id": "using/deleted",
  "title": "deleted",
  "description": "The option window",
  "source": "@site/docs/using/deleted.md",
  "permalink": "/ScummDocs/using/deleted",
  "editUrl": "https://github.com/ScummDocs/docs/using/deleted.md"
};
  /* @jsx mdx */

export const rightToc = [{
  value: 'The option window',
  id: 'the-option-window',
  children: []
}, {
  value: 'The game configuration window',
  id: 'the-game-configuration-window',
  children: []
}, {
  value: 'You see this game configuration window whenever you add a game to the Launcher or click on Edit Game button. Here you can configure a game individually by overriding the default game settings you have set in the Launcher&#39;s Options, as well as change the game&#39;s language and original platform (if applicable). For more information, see &quot;Configuring a Game in ScummVM&quot;.',
  id: 'you-see-this-game-configuration-window-whenever-you-add-a-game-to-the-launcher-or-click-on-edit-game-button-here-you-can-configure-a-game-individually-by-overriding-the-default-game-settings-you-have-set-in-the-launchers-options-as-well-as-change-the-games-language-and-original-platform-if-applicable-for-more-information-see-configuring-a-game-in-scummvm',
  children: []
}, {
  value: 'The Global Main Menu',
  id: 'the-global-main-menu',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "the-option-window"
    }}>{`The option window`}</h2>
    <img alt=" " src={useBaseUrl('img/Launcher/Graphicstab.jpg')} />
    <p>{`This menu allows you to define default game options, as well as change a number of the ScummVM Launcher's settings. For more details on how to fine-tune ScummVM and pre-configure games, see "Configuring ScummVM". `}</p>
    <hr></hr>
    <h2 {...{
      "id": "the-game-configuration-window"
    }}>{`The game configuration window`}</h2>
    <img alt=" " src={useBaseUrl('img/Launcher/Configdone.jpg')} />
    <h2 {...{
      "id": "you-see-this-game-configuration-window-whenever-you-add-a-game-to-the-launcher-or-click-on-edit-game-button-here-you-can-configure-a-game-individually-by-overriding-the-default-game-settings-you-have-set-in-the-launchers-options-as-well-as-change-the-games-language-and-original-platform-if-applicable-for-more-information-see-configuring-a-game-in-scummvm"
    }}>{`You see this game configuration window whenever you add a game to the Launcher or click on Edit Game button. Here you can configure a game individually by overriding the default game settings you have set in the Launcher's Options, as well as change the game's language and original platform (if applicable). For more information, see "Configuring a Game in ScummVM".`}</h2>
    <h2 {...{
      "id": "the-global-main-menu"
    }}>{`The Global Main Menu`}</h2>
    <img alt=" " src={useBaseUrl('img/Launcher/Generalmenu.jpg')} />
    <p>{`The Global Menu (or GMM for Global Main Menu) is a feature that premiered in ScummVM 0.13.0. It is available in all games by pressing `}<inlineCode parentName="p">{`Crtl+F5`}</inlineCode>{` and provides the following features:`}</p>
    <p><strong parentName="p">{`Resume:`}</strong>{` close the GMM and resume the game.
`}<strong parentName="p">{`Load:`}</strong>{` load a game state (this is not available in all games).
`}<strong parentName="p">{`Save:`}</strong>{` save a game state (this is not available in all games).
`}<strong parentName="p">{`Options:`}</strong>{` change some options to play the game (e.g. music volume, subtitles speed).
`}<strong parentName="p">{`About:`}</strong>{` display the ScummVM About box (also available from the The Launcher).
`}<strong parentName="p">{`Help:`}</strong>{` displays a list of key commands (not available in all engines)
`}<strong parentName="p">{`Return to Launcher:`}</strong>{` quit the game and return to The Launcher (not available in all engines)
`}<strong parentName="p">{`Quit:`}</strong>{` quit the game and ScummVM and return to the Operating System.`}</p>
    <p>{`In some games you can still access the original game menu by using Alt-F5. You can save and load games using this, however it is not intended for this purpose, and may even crash ScummVM in some games. `}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
