  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {};
export const metadata = {
  "id": "installation/deleted_rpi_steps",
  "title": "deleted_rpi_steps",
  "description": "1. Create a new folder.",
  "source": "@site/docs/installation/deleted_rpi_steps.md",
  "permalink": "/installation/deleted_rpi_steps",
  "editUrl": "https://github.com/ScummDocs/docs/installation/deleted_rpi_steps.md"
};
  /* @jsx mdx */

export const rightToc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <ol>
      <li parentName="ol">{`Create a new folder.`}</li>
    </ol>
    <ul>
      <li parentName="ul">{`To use the command line, change your present working directory to the directory in which you wish to create the new directory. To do this, use `}<inlineCode parentName="li">{`cd`}</inlineCode>{` followed by the path. The tilde `}<inlineCode parentName="li">{`~`}</inlineCode>{` represents the home directory, so in this example the directory is changed to the home directory. `}</li>
    </ul>
    <pre><code parentName="pre" {...{}}>{`    cd ~
`}</code></pre>
    <ul>
      <li parentName="ul">{`Use `}<inlineCode parentName="li">{`mkdir`}</inlineCode>{` to create a new directory. In this example, it is called 'mygames' in the /pi/home/ directory. `}</li>
    </ul>
    <pre><code parentName="pre" {...{}}>{`    mkdir mygames
`}</code></pre>
    <ol {...{
      "start": 2
    }}>
      <li parentName="ol">{`Copy the folder containing the game data files into the folder you just created. `}</li>
    </ol>
    <ul>
      <li parentName="ul">{`To use the command line, use `}<inlineCode parentName="li">{`cp`}</inlineCode>{` to copy the directory of game files. This command works like a "Copy/Paste", where the first variable is the directory to be copied, and the second is the directory it is pasted to.
In this example, `}<inlineCode parentName="li">{`~/myfirstgame/`}</inlineCode>{` is the path to the directory containing the game files of the game.`}</li>
    </ul>
    <pre><code parentName="pre" {...{}}>{`    cp ~/myfirstgame/ ~/mygames/
`}</code></pre>
    <p>{`The directory structure should look something like the following:`}</p>
    <pre><code parentName="pre" {...{}}>{`mygames
|-- myfirstgame
|   |-- readme.txt
|   |-- firstgame.cpt
|   |-- firstgame.dnr
|   \`-- firstgame.dsk
\`-- mysecondgame
    |-- secondgame.1c
    \`-- readme.txt
`}</code></pre>
    <ol {...{
      "start": 3
    }}>
      <li parentName="ol">{`Repeat step 2 as required to copy any further games into the folder.  `}</li>
    </ol>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
